import Asteroid from './asteroidC';
import Enemy from './enemyC';

class Spawner extends Phaser.GameObjects.Sprite{

    constructor (scene, x, y, enemyType, spawnDelay, maxEnemies, player) {
        super(scene, x, y, enemyType, spawnDelay, maxEnemies, player);

        // making spawner invisible
        this.visible = false;

        // adding this to scene
        this.scene.add.existing(this);

        // enemy Spawning variables
        this.enemyGroup = scene.add.group();
        this.nextEnemyTime = 0;
        this.spawnDelay = spawnDelay;
        this.enemyType = enemyType;
        this.maxEnemies = maxEnemies;
        this.player = player;
        this.noEnemies = 1;
    }

    // called regularly from game.js update()
    update() {

        this.spawn();
        this.collisions();

        // putting enemies into an array so they can be updated
        let enemy_ary = this.enemyGroup.getChildren();
        enemy_ary.forEach(enemy => {
            enemy.update();
        });
    }

    spawn() {
        // if max enemies has not yet been hit
        if (this.noEnemies <= this.maxEnemies) {
            // if enough time has passed
            if (this.scene.time.now > this.nextEnemyTime) {
                // initializing new enemy

                switch (this.enemyType) {
                    case "enemy":
                        this.enemyGroup.add(new Enemy(this.scene, this.x, this.y, 'enemy_img'));
                        this.noEnemies += 1;
                        break;
                    case "asteroid":
                        this.enemyGroup.add(new Asteroid(this.scene, this.x, this.y, 'asertoid_img'));
                        this.noEnemies += 1;
                        break;
                }

                // updating nextEnemyTime to maintain delay between spawning
                this.nextEnemyTime = this.scene.time.now + this.spawnDelay;
            }
        }
    }

    collisions() {
    
        // playerBullets vs enemies
        this.scene.physics.add.collider(this.player.bulletGroup, this.enemyGroup, function(bullet, enemy){
            bullet.destroy();
            enemy.destroy();
            score += enemy.getScore();
        });
    
        // checking each enemy
        let enemy_ary = this.enemyGroup.getChildren();
        enemy_ary.forEach(function(enemy) {
            // enemy bullets vs player
            enemy.scene.physics.add.collider(this.player, enemy.bulletGroup, function(player, bullet) {
                bullet.destroy();
                player.setLives(-1);
            });
    
            // checking if enemy has hit player
            enemy.scene.physics.add.collider(this.player, enemy, function(player, enemy){
                enemy.destroy();
                player.setLives(-1);
            })
        });
    }
}

export default Spawner;
